<template>
	<view class="product">
		<view class="detail">采购明细</view>
		<view class="cu-form-group">
			<text :style="{color:'white'}">*</text>
			<view class="title">采购照片：</view>
			<upimg @photo="getPhoto" :photo="purchaseOrder.photo"></upimg>
		</view>
		<view class="cu-form-group">
			<text :style="{color:'white'}">*</text>
			<view class="title">发&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;票：</view>
			<upimg @photo="getInvoice" :photo="purchaseOrder.invoice"></upimg>
		</view>
		<view class="cu-form-group">
			<text :style="{color:'red'}">*</text>
			<view class="title">发票日期：</view>
			<picker mode="date" :value="purchaseOrder.invoiceDate" @change="bindDateChange">
				<view class="title">{{purchaseOrder.invoiceDate==null?"请选择发票日期":purchaseOrder.invoiceDate}}</view>
			</picker>
		</view>
		<view class="cu-form-group">
			<text :style="{color:'red'}">*</text>
			<view class="title">采购单价：</view>
			<input type="digit" placeholder="请输入采购单价" v-model="purchaseOrder.price" @input="checkPrice"></input>
		</view>
		<view class="cu-form-group">
			<text :style="{color:'red'}">*</text>
			<view class="title">已采购数量：</view>
			<input type="number" placeholder="请输入已采购数量" v-model="purchaseOrder.purchasedQuantity" @input="checkQuantity"></input>
		</view>
		<view class="cu-form-group">
			<text :style="{color:'white'}">*</text>
			<view class="title">应采购数量：{{purchaseOrder.quantity}}</view>
		</view>
		<view class="cu-form-group">
			<text :style="{color:'white'}">*</text>
			<view class="title">是否创建新采购单：</view>
			<switch @change="switchChange" :checked="needSplit"/>
		</view>
		<view class="detail">商品信息</view>
		<view class="cu-form-group">
			<view class="title">商品名称：{{purchaseOrder.product.name}}</view>
		</view>
		<view class="cu-form-group">
			<view class="title">商品品牌：{{purchaseOrder.product.brand}}</view>
		</view>
		<view class="cu-form-group">
			<view class="title">商品型号：{{purchaseOrder.product.specification}}</view>
		</view>
		<view class="cu-form-group">
			<view class="title">商品条码：{{purchaseOrder.product.barcode}}</view>
		</view>
		<view class="cu-form-group">
			<view class="title">商品图片：</view>
			<myimg :photo="purchaseOrder.product.image" :padding="true"></myimg>
		</view>
		<view class="cu-form-group" v-if="purchaseOrder.product.manufacturer">
			<view class="title">生产厂家：{{purchaseOrder.product.manufacturer}}</view>
		</view>
		<view class="cu-form-group" v-if="purchaseOrder.product.origin">
			<view class="title">生产地区：{{purchaseOrder.product.origin}}</view>
		</view>
		<view class="cu-form-group" v-if="purchaseOrder.product.remark">
			<view class="title">备注信息：{{purchaseOrder.product.remark}}</view>
		</view>
		<view class="H50"></view>
		<view class="o_btn">
			<view class="flex flex-direction">
				<button class="cu-btn bg-red margin-tb-sm lg" @click="sub()">上传信息</button>
			</view>
		</view>
	</view>
</template>

<script>
	export default {
		data() {
			return {
				purchaseOrder: {"id":16,"createTime":"2021-12-27 20:12:48","updateTime":"2021-12-29 23:52:45","buyer":{"id":9,"createTime":"2021-12-27 00:37:19","updateTime":"2021-12-27 00:37:19","username":"15858780802","password":"$2a$10$Ro7YEGkUtXDS5AJdEkj1BeNSg0qSB.A6xbSLoe41sWSeSmvt85PAS","name":"采购","role":"BUYER","phoneNumber":null},"status":"READY","invoiceDate":"2021-12-30","product":{"id":2,"createTime":"2021-12-26 21:54:42","updateTime":"2021-12-28 17:20:44","name":"测试修改2","barcode":"6922266446726","brand":"清风","specification":"150抽/包","inventory":{"id":2,"createTime":"2021-12-26 21:54:42","updateTime":"2021-12-29 20:52:52","warehouseInventory":1,"hubInventory":10,"midwayInventory":1},"manufacturer":null,"origin":"湖北省孝南市","remark":null,"customsInfo":{"id":2,"createTime":"2021-12-26 21:54:42","updateTime":"2021-12-28 17:20:44","hsCode":"42022900","materialBeschaffenheit":"This version is in a nano size in classic calfskin.Shoulder, crossbody, top handle or clutch carry. Detachable chain. shoulder strap. Zip closure with calfskin pull. Customisable with strap and personalised charms. Herringbone cotton canvas lining Embossed Anagram","brandArticleNo":"A510U98X01","brand":"LOEWE","articleName":"Nano Puzzle bag in classic calfskin","unitPrice":54096},"image":"data:image/jpeg;base64,"},"purchasePrice":2533,"quantity":1,"rejectReason":null,"warehouseKeeper":null,"photo":"data:image/png;base64,","invoice":"data:image/png;base64,","price":"25.33"},
				date: '请选择日期',
				needSplit: false,
			};
		},
		onLoad(option) {
			this.purchaseOrder = JSON.parse(decodeURIComponent(option.purchaseOrder));
		},
		methods: {
			getPhoto(val) {
				this.purchaseOrder.photo = val
			},
			getInvoice(val) {
				this.purchaseOrder.invoice = val
			},
			checkPrice: function(e) {
				//正则表达式
				e.target.value = (e.target.value.match(/^\d*(\.?\d{0,2})/g)[0]) || null
				//重新赋值给input				
				this.$nextTick(() => {
					this.purchaseOrder.price = e.target.value
					this.purchaseOrder.purchasePrice = this.purchaseOrder.price * 100
				})
			},
			checkQuantity: function(e) {
				//正则表达式
				e.target.value = e.target.value > this.purchaseOrder.quantity ? this.purchaseOrder.quantity : e.target.value;
				//重新赋值给input				
				this.$nextTick(() => {
					this.purchaseOrder.purchasedQuantity = e.target.value
				})
			},
			sub() {
				let rules = [
					{name: 'photo', required: true, type: 'required', errmsg: '请上传商品图片'},
					{name: 'invoice', required: true, type: 'required', errmsg: '请上传发票'},
					{name: 'invoiceDate', required: true, type: 'required', errmsg: '请输入发票日期'},
					{name: 'price', required: true, type: 'required', errmsg: '请输入采购单价'},
					{name: 'purchasedQuantity', required: true, type: 'required', errmsg: '请输入已采购数量'}
				]
				let valLoginRes = this.$validate.validate(this.purchaseOrder, rules)
				if (!valLoginRes.isOk) {
					uni.showToast({
						icon: 'none',
						title: valLoginRes.errmsg
					})
				} else {
					var param = this.needSplit?'true':'false';
					this.$api.http.put('/purchaseOrder/uploadPurchaseInfo?needSplit='+param, this.purchaseOrder).then(res => {
						this.$api.msg.successToast('上传成功').then(res => {
							uni.$emit('edit');
							uni.redirectTo({
								url: './Purchaser'
							})
						})
					})
				}	
			},
			bindDateChange: function(e) {
				this.purchaseOrder.invoiceDate = e.target.value;
			},
			getScanCode() {
				let _this = this;
				uni.scanCode({
					scanType:['barCode'],
					success: function (res) {
						_this.purchaseOrder.product.barcode = res.result;
					}
				})
			},
			switchChange: function (e) {
				this.needSplit = e.target.value
			}
		}
	}
</script>

<style lang="less">
	page {
		background-color: #F7F6FB;
	}
	.o_btn {
		background: #FFFFFF;
		padding: 0 10px 0px;
		position: fixed;
		bottom: 0;
		width: 100%;
		z-index: 9999;
	}
	// 去掉 picker 右边的箭头
	.cu-form-group picker::after {  
	    display: none;  
	}  
</style>
